from python.rna_to_protein_sequence_translation import translate_to_protein


def test_conversion() -> None:
    assert translate_to_protein('AUG') == 'M'
    assert translate_to_protein('AUGUGA') == 'M'
    assert translate_to_protein('AUGGUUAGUUGA') == 'MVS'
    assert translate_to_protein('UGCGAUGAAUGGGCUCGCUCC') == 'CDEWARS'
    assert translate_to_protein('AUGUCCUUCCAUCAAGGAAACCAUGCGCGUUCAGCUUUCUGA') == 'MSFHQGNHARSAF'
    assert translate_to_protein('AUGCUUCAAGUGCACUGGAAAAGGAGAGGGAAAACCAGUUGA') == 'MLQVHWKRRGKTS'
    assert translate_to_protein('AUGGCGUUCAGCUUUCUAUGGAGGGUAGUGUACCCAUGCUGA') == 'MAFSFLWRVVYPC'
    assert translate_to_protein('AUGCAGCUUUCUAUGGAGGGUAGUGUUAACUACCACGCCUGA') == 'MQLSMEGSVNYHA'
    assert translate_to_protein('AUGCUAUGGAGGGUAGUGUUAACUACCACGCCCAGUACUUGA') == 'MLWRVVLTTTPST'
    assert translate_to_protein('') == 'MYPSIKETMRVQLSMEGSVNYHAFKCTGKGEGKPYEGTQSLNITITEGGPLPFAFDILSHAFQYGIKVFAKYPKEIPDFFKQSLPGGFSWERVSTYEDGGVLSATQETSLQGDCIICKVKVLGTNFPANGPVMQKKTCGWEPSTETVIPRDGGLLLRDTPALMLADGGHLSCFMETTYKSKKEVKLPELHFHHLRMEKLNISDDWKTVEQHESVVASYSQVPSKLGHN'

